import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'

export class News extends Component {
  static defaultProps = {
    country: 'in',
    pagesize : 5,
    category : "general"
  }

  static propTypes = {
    country: PropTypes.string,
    pagesize: PropTypes.number,
    category: PropTypes.string
  }
  
  capitalizeFirstLetter = (string) => {
    return String(string).charAt(0).toUpperCase() + String(val).slice(1);
 }
  
  constructor(props){
    super(props);
    console.log("Hello I am a constructor from new component")
    this.state = {
        articles: [],
        loading: false,
        page: 1
    }
    document.title = `${this.props.category} - NewsMonkey`;
  }

  async updateNews(){
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=5f18015b784b4b12b40872e3d7d00e23&page=${this.state.page}&pageSize=${this.props.pagesize}`;
     this.setState({loading: true});
     let data = await fetch(url);
     let parsedData = await data.json();
     console.log(parsedData);
     this.setState({
      articles: parsedData.articles, 
      totalResults: parsedData.totalResults,
      loading: false
    })
  }

  async componentDidMount(){
     this.updateNews();
  }

  handlePreviousClick = async () =>{
    // console.log("Previous");

    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=5f18015b784b4b12b40872e3d7d00e23&page=${this.state.page - 1}&pageSize=${this.props.pagesize}`;
    // this.setState({loading: true});
    // let data = await fetch(url);
    // let parsedData = await data.json()
    // console.log(parsedData);
    // this.setState({
    //   page: this.state.page - 1,
    //   articles: parsedData.articles,
    //   loading: false
    // })
    this.setState({page : this.state.page - 1});
    this.updateNews();
  }

  handleNextClick = async () =>{
  //   console.log("Next");
  //   if(!(this.state.page+1>Math.ceil(this.state.totalResults/this.props.pagesize))){
  //   let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=5f18015b784b4b12b40872e3d7d00e23&page=${this.state.page + 1}&pageSize=${this.props.pagesize}`;
  //   this.setState({loading: true});
  //    let data = await fetch(url);
  //    let parsedData = await data.json()
  //    console.log(parsedData);
  //   this.setState({
  //     page: this.state.page + 1,
  //     articles: parsedData.articles,
  //     loading: false
  //   })
  // }
  this.setState({page : this.state.page + 1});
  this.updateNews();
 }

  render() {
    return (
      <div className='container my-3'> 
      <h1 className='text-center'>NewMonkey - Top Headlines</h1>
      {this.state.loading && <Spinner/>}
        <div className='row'>
        {!this.state.loading && this.state.articles.map((element) => {
          return <div className='col-md-4' key={element.url}>
            <NewsItem title={element.title?element.title.slice(0,45):"" } description={ element.description?element.description.slice(0,88):""}
            imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name}/>
          </div>
        })}
        
        </div>
        <div className="container d-flex justify-content-between" >
        <button disabled =  {this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePreviousClick}>&laquo; Previous</button>
        <button disabled =  {this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pagesize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next  &raquo;</button>

        </div>
      </div>
    )
  }
}

export default News
